
public class Enhancedforloop {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

	int a[] = {1,2,3,4,5,6};
	
	for(int k : a)
	{
		System.out.println(k);
	}
	
	System.out.println();
	
	int b[]= {10,20,30};
	
	for(int i=0;i<b.length;i++)
	{
		System.out.println(b[i]);
	}
	
	}

}
